from typing import List, Optional, Tuple, Union

k_colors_beg = [
        "\033[38;5;196m", "\033[38;5;202m", "\033[38;5;208m", "\033[38;5;214m", "\033[38;5;220m",
        "\033[38;5;226m", "\033[38;5;190m", "\033[38;5;154m", "\033[38;5;118m", "\033[38;5;82m",
    ]
k_colors_end = "\033[0m"

kcb = [0.2, 0.4, 0.6, 0.7, 0.75, 0.8, 0.85, 0.9, 0.94, 0.97, 1.1]


def build_string(texts: List[str], confidence: List[float]):
    s = []
    for t, c in zip(texts, confidence):
        ii = 0
        for i in range(len(kcb)):
            # print(kcb[i-1], c, kcb[i])
            if kcb[i-1] < c < kcb[i]:
                ii = i-1
                break
        # print(ii)
        s.append(k_colors_beg[ii] + t + k_colors_end)
    return s


def test_confidence(sequences: list):
    # -- print sequence
    texts = []
    conf = []
    for s in sequences:
        texts.append(s['text'])
        conf.append(s['avg_confidence'])
    s = build_string(texts, conf)
    print('Sequences:')
    [print(x) for x in s]
    # -- print words:
    texts = []
    conf = []
    for s in sequences:
        for x in s['whole_word_timestamps']:
            texts.append(x['word'])
            conf.append(x['confidence'])
    s = build_string(texts, conf)
    print('Words:')
    print(''.join(s))


